	public Boolean checkPassword(User user) {

		int passwordLength = user.getPassword().length();

		if (passwordLength >= 6) {
			return true;
		} else {
			System.out.println("Parola minimum 6 karakterden oluþmalýdýr.");
			return false;
		}
	}

	public Boolean checkFirstName(User user) {

		int firstNameLength = user.getFirstName().length();

		if (firstNameLength > 2) {
			return true;
		} else {
			System.out.println("Adýnýz minimum 2 karakterden oluþmalýdýr.");
			return false;
		}

	}

	public Boolean checkLastName(User user) {

		int lastNameLength = user.getLastName().length();

		if (lastNameLength > 2) {
			return true;
		} else {
			System.out.println("Soyadýnýz minimum 2 karakterden oluþmalýdýr.");
			return false;
		}

	}

	public Boolean compareEmails(User member) {

		for (User user : UserLists.Liste /* this.userKontrolList */) {
			// System.out.println(use.getEmail());
			if (user.getEmail() == member.getEmail()) {
				System.out.println(
						"Email adresi daha önce kullanýlmýþ.\n---------------------------------------------------------------------------------------------------------------------------");
				return false;
			}
		}
		// userKontrolList.add(user);
		return true;
	}

	@Override
	public Boolean loginRules(User member) {
		for (User user : UserLists.Liste) {

			if (user.getEmail() == member.getEmail() && user.getPassword() == member.getPassword()) {
				return true;
			}
		}

		return false;
	}

	public Boolean loginRules2(Auth auth) {

		for (User user : UserLists.Liste) {

			if (user.getEmail() == auth.getEmail() && user.getPassword() == auth.getPassword()) {
				return true;
			}
		}

		return false;
	}

	public Boolean CheckAll(Boolean... args) {

		for (Boolean arg : args) {

			if (arg) {
				return true;
			}
		}
		return false;
	}

	public Boolean CheckDoðrulamakodu(int dogrumalaKodu, int input) {

		if (dogrumalaKodu == input) {
			System.out.println("Doðrulama Kodu Doðru Teþekkürler!\n---------------------------------------------------------------------------------------------------------------------------");
			return true;
		}
		System.out.println("Doðrulama Kodu Yanlýþ Tekrar Kontrol Ediniz.\n---------------------------------------------------------------------------------------------------------------------------");
		return false;
	}
